mapper.update:
UPDATE t_board
set hits = hits+1
,lastip = #{lastip}
where iboard = ${iboard}

update t_board


        title = #{title},


        ctnt = #{ctnt},


        isdel = 1


where iboard = ${iboard}
and iuser = ${iuser}

mapper.delete:
update t_board
set isdel = 1
where iboard = ${iboard}
